import { srcImgOriginal, srcCardImg } from "@/lib/api.service";
import { Button, Col, Row } from "antd";
import Image from "next/image";
import React, { useEffect, useState } from "react";
import styles from "@/styles/MovieDetailImage.module.css";
import { FaRegPlayCircle } from "react-icons/fa";
import Link from "next/link";
import ButtonVipMode from "../ButtonVipMode";
import ButtonAddToCollection from "../ButtonAddToCollection";
import ChooseToBuy from "../ChooseToBuy";

export default function MovieDetailImage({
  pathImg,
  title = "title",
  posterImg,
  subtitle = "sub title",
  id,
}) {
  const fallBack =
    "data:image/png;base64,";
  const [posterSrc, setPosterSrc] = useState(fallBack);
  const [dropSrc, setDropSrc] = useState(fallBack);

  useEffect(() => {
    if (posterImg) {
      setPosterSrc(srcCardImg + posterImg);
    }

    if (pathImg) {
      setDropSrc(srcImgOriginal + pathImg);
    }
  }, [posterImg, pathImg]);

  return (
    <Row>
      <Col span={24}>
        <div className={styles.imgBig}>
          <Image src={dropSrc} fill sizes="auto" title={title} alt={title} />
        </div>
        <div className={styles.info__wrap}>
          <Row wrap={false} gutter={16} align="bottom">
            <Col span={0} lg={6}>
              <div className={styles.img__small}>
                <Image
                  src={posterSrc}
                  fill
                  sizes="auto"
                  title={title}
                  alt={title}
                />
              </div>
            </Col>
            <Col span={24} lg={18}>
              <div className={styles.film__infoText}>
                <h1>{title}</h1>
                <h2>{subtitle}</h2>
                <ul className={styles.listButton}>
                  <li>
                    <Link href={`/watch/${id}`}>
                      <Button
                        type="primary"
                        size="large"
                        danger
                        className={styles.wrap__button}
                      >
                        <FaRegPlayCircle />
                        Xem Phim
                      </Button>
                    </Link>
                  </li>
                  <li>
                    <ButtonVipMode>
                      <ChooseToBuy id={id} />
                    </ButtonVipMode>
                  </li>
                  <li>
                    <ButtonAddToCollection
                      size="large"
                      className={styles.buttonCollection}
                      id={id}
                    />
                  </li>
                </ul>
              </div>
            </Col>
          </Row>
        </div>
      </Col>
    </Row>
  );
}
